import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useForm } from 'react-hook-form';

type FormData = {
  name: string;
  email: string;
  subject: string;
  message: string;
  phone?: string;
  company?: string;
  preferredContact: 'email' | 'phone';
};

const Contact: React.FC = () => {
  const { register, handleSubmit, formState: { errors }, reset } = useForm<FormData>();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState<'success' | 'error' | null>(null);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [availability, setAvailability] = useState<string>('Available');

  // Simulate real-time availability status
  useEffect(() => {
    const now = new Date();
    const hour = now.getHours();
    if (hour >= 9 && hour < 17) {
      setAvailability('Available');
    } else if (hour >= 17 && hour < 22) {
      setAvailability('Available with delayed response');
    } else {
      setAvailability('Away - will respond within 24 hours');
    }
  }, []);

  const onSubmit = async (data: FormData) => {
    setIsSubmitting(true);
    try {
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Create a more detailed email body
      const emailBody = `
From: ${data.name}
Email: ${data.email}
${data.phone ? `Phone: ${data.phone}` : ''}
${data.company ? `Company: ${data.company}` : ''}
Preferred Contact Method: ${data.preferredContact}

Message:
${data.message}
      `;
      
      const mailtoLink = `mailto:mattdarabasz@gmail.com?subject=${encodeURIComponent(data.subject)}&body=${encodeURIComponent(emailBody)}`;
      window.location.href = mailtoLink;
      
      setSubmitStatus('success');
      setShowSuccessModal(true);
      reset();
    } catch (error) {
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 0.4 }
    }
  };

  const contactMethods = [
    {
      icon: "fas fa-envelope",
      title: "Email",
      value: "mattdarabasz@gmail.com",
      link: "mailto:mattdarabasz@gmail.com"
    },
    {
      icon: "fas fa-phone",
      title: "Phone",
      value: "(215) 303-7737",
      link: "tel:2153037737"
    },
    {
      icon: "fab fa-linkedin",
      title: "LinkedIn",
      value: "Connect with me",
      link: "https://www.linkedin.com/in/matthew-d-758a27168/"
    },
    {
      icon: "fab fa-github",
      title: "GitHub",
      value: "View my repositories",
      link: "https://github.com/dasz4376"
    }
  ];

  return (
    <motion.section 
      initial="hidden"
      animate="visible"
      variants={containerVariants}
      className="min-h-screen pt-24 pb-12 px-4 bg-gradient-to-b from-slate-900/95 to-slate-800/90"
    >
      <div className="max-w-6xl mx-auto">
        <div className="bg-slate-800/30 rounded-xl p-6">
          {/* Header */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center mb-8"
          >
            <h2 className="text-4xl font-bold text-white mb-4">
              Get in Touch
            </h2>
            <p className="text-gray-300 text-lg max-w-2xl mx-auto">
              Let's discuss how I can help with your next project
            </p>
          </motion.div>

          {/* Availability Badge */}
          <div className="flex justify-center mb-6">
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className={`px-4 py-2 rounded-lg ${
                availability === 'Available' 
                  ? 'bg-green-500/10 text-green-400 border border-green-500/20'
                  : availability.includes('delayed')
                    ? 'bg-yellow-500/10 text-yellow-400 border border-yellow-500/20'
                    : 'bg-red-500/10 text-red-400 border border-red-500/20'
              } flex items-center space-x-2`}
            >
              <span className="relative flex h-2 w-2">
                <span className={`animate-ping absolute inline-flex h-full w-full rounded-full opacity-75 ${
                  availability === 'Available' ? 'bg-green-400' : 'bg-yellow-400'
                }`}></span>
                <span className={`relative inline-flex rounded-full h-2 w-2 ${
                  availability === 'Available' ? 'bg-green-500' : 'bg-yellow-500'
                }`}></span>
              </span>
              <span className="text-sm font-medium">{availability}</span>
            </motion.div>
          </div>

          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="grid md:grid-cols-2 gap-8"
          >
            {/* Contact Methods */}
            <div className="space-y-4">
              <h3 className="text-xl font-semibold text-white mb-4">
                Contact Information
              </h3>
              
              <div className="grid gap-4">
                {contactMethods.map((method, index) => (
                  <motion.a
                    key={index}
                    href={method.link}
                    target={method.icon.includes('fa-') ? "_blank" : undefined}
                    rel={method.icon.includes('fa-') ? "noopener noreferrer" : undefined}
                    className="group flex items-center p-4 rounded-lg bg-slate-800/20 hover:bg-slate-700/20 transition-all duration-300"
                    whileHover={{ scale: 1.01 }}
                    whileTap={{ scale: 0.99 }}
                  >
                    <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center mr-3 group-hover:bg-primary/20 transition-colors duration-300">
                      <i className={`${method.icon} text-xl text-primary`}></i>
                    </div>
                    <div>
                      <h4 className="text-white font-medium">{method.title}</h4>
                      <p className="text-gray-300 text-sm">{method.value}</p>
                    </div>
                  </motion.a>
                ))}
              </div>
            </div>

            {/* Contact Form */}
            <div className="bg-slate-800/20 p-6 rounded-lg">
              <h3 className="text-xl font-semibold text-white mb-4">
                Send a Message
              </h3>

              <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                <div>
                  <input
                    {...register('name', { required: 'Name is required' })}
                    type="text"
                    placeholder="Your Name"
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                  {errors.name && (
                    <p className="text-red-400 text-sm mt-1">{errors.name.message}</p>
                  )}
                </div>

                <div>
                  <input
                    {...register('email', { 
                      required: 'Email is required',
                      pattern: {
                        value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                        message: 'Invalid email address'
                      }
                    })}
                    type="email"
                    placeholder="Your Email"
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                  {errors.email && (
                    <p className="text-red-400 text-sm mt-1">{errors.email.message}</p>
                  )}
                </div>

                <div>
                  <input
                    {...register('subject', { required: 'Subject is required' })}
                    type="text"
                    placeholder="Subject"
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                  {errors.subject && (
                    <p className="text-red-400 text-sm mt-1">{errors.subject.message}</p>
                  )}
                </div>

                <div>
                  <textarea
                    {...register('message', { required: 'Message is required' })}
                    placeholder="Your Message"
                    rows={4}
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                  {errors.message && (
                    <p className="text-red-400 text-sm mt-1">{errors.message.message}</p>
                  )}
                </div>

                <div>
                  <input
                    {...register('phone')}
                    type="tel"
                    placeholder="Phone (optional)"
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                </div>

                <div>
                  <input
                    {...register('company')}
                    type="text"
                    placeholder="Company (optional)"
                    className="w-full px-4 py-2 rounded-lg bg-slate-700/30 text-white placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-primary/30 border border-slate-600/30"
                  />
                </div>

                <div className="space-y-2">
                  <label className="text-white text-sm font-medium">Preferred Contact Method</label>
                  <div className="flex space-x-4">
                    <label className="flex items-center space-x-2">
                      <input
                        {...register('preferredContact')}
                        type="radio"
                        value="email"
                        defaultChecked
                        className="text-primary focus:ring-primary"
                      />
                      <span className="text-gray-300 text-sm">Email</span>
                    </label>
                    <label className="flex items-center space-x-2">
                      <input
                        {...register('preferredContact')}
                        type="radio"
                        value="phone"
                        className="text-primary focus:ring-primary"
                      />
                      <span className="text-gray-300 text-sm">Phone</span>
                    </label>
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="w-full px-6 py-3 bg-primary/80 text-white font-medium rounded-lg hover:bg-primary transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isSubmitting ? 'Sending...' : 'Send Message'}
                </button>
              </form>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Success Modal */}
      <AnimatePresence>
        {showSuccessModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-center justify-center z-50"
            onClick={() => setShowSuccessModal(false)}
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-slate-800/90 p-6 rounded-xl max-w-sm w-full mx-4 text-center"
            >
              <div className="w-12 h-12 bg-green-500/10 rounded-full flex items-center justify-center mx-auto mb-3">
                <i className="fas fa-check text-xl text-green-400"></i>
              </div>
              <h3 className="text-xl font-bold text-white mb-2">Message Sent!</h3>
              <p className="text-gray-300 text-sm mb-4">
                Thank you for your message. I'll get back to you as soon as possible.
              </p>
              <button
                onClick={() => setShowSuccessModal(false)}
                className="px-4 py-2 bg-primary/80 text-white font-medium rounded-lg hover:bg-primary transition-all duration-300"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.section>
  );
};

export default Contact;
