import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Home: React.FC = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const [typedText, setTypedText] = useState('');
  const fullText = "Transforming Healthcare Technology & Building Innovative Solutions";
  const [showHighlight, setShowHighlight] = useState(false);

  const [activeTab, setActiveTab] = useState('all');
  const [ref2] = useInView({ threshold: 0.1, triggerOnce: true });

  const highlights = [
    {
      icon: "fas fa-laptop-medical",
      title: "Healthcare Technology",
      description: "Leading digital transformation in healthcare with secure, compliant, and user-friendly solutions.",
      tags: ["HIPAA Compliance", "EHR Integration", "Patient Care", "Healthcare IT"]
    },
    {
      icon: "fas fa-code",
      title: "Full-Stack Development",
      description: "Building scalable applications with modern technologies and best practices.",
      tags: ["React", "TypeScript", "C#", "SQL", "REST APIs"]
    },
    {
      icon: "fas fa-project-diagram",
      title: "Technical Leadership",
      description: "Driving innovation and leading development teams to deliver high-impact solutions.",
      tags: ["Team Leadership", "Project Management", "System Architecture", "Agile"]
    }
  ];

  const achievements = [
    {
      title: "Healthcare Technology Innovation",
      role: "Paragon Wellness Center",
      content: "Led development of integrated healthcare systems, improving patient care efficiency and data management.",
      icon: "fas fa-hospital-user"
    },
    {
      title: "Gaming Platform Development",
      role: "FiveM Development",
      content: "Architected and implemented custom gaming systems serving multiple concurrent users with real-time interactions.",
      icon: "fas fa-gamepad"
    }
  ];

  useEffect(() => {
    let index = 0;
    const timer = setInterval(() => {
      setTypedText(fullText.slice(0, index));
      index++;
      if (index > fullText.length) {
        clearInterval(timer);
        setShowHighlight(true);
      }
    }, 50);
    return () => clearInterval(timer);
  }, []);

  const stats = [
    { number: '4+', label: 'Years Experience', icon: 'fas fa-clock' },
    { number: '20+', label: 'Projects Delivered', icon: 'fas fa-tasks' },
    { number: '10+', label: 'Technologies', icon: 'fas fa-code-branch' },
    { number: '2+', label: 'Industries', icon: 'fas fa-industry' }
  ];

  return (
    <>
      {/* Hero Section */}
      <motion.section 
        ref={ref}
        initial={{ opacity: 0 }}
        animate={{ opacity: inView ? 1 : 0 }}
        transition={{ duration: 0.8 }}
        className="min-h-screen flex flex-col items-center justify-center px-4 space-y-16 pt-20 md:pt-0 relative overflow-hidden"
      >
        {/* Background gradient */}
        <div className="absolute inset-0 bg-gradient-to-b from-slate-800/95 via-slate-900/90 to-slate-950/85"></div>
        
        <div className="relative text-center max-w-4xl mx-auto z-10">
          <div className="space-y-8">
            <motion.h1 
              className="text-4xl md:text-7xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-primary via-purple to-info"
              initial={{ y: -50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
            >
              Matthew Darabasz
            </motion.h1>
            
            <motion.div 
              className="flex justify-center"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.5, delay: 0.3 }}
            >
              <div className="h-1 w-48 bg-gradient-to-r from-primary via-purple to-info rounded-full"></div>
            </motion.div>
            
            <motion.div 
              className="space-y-4"
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <h2 className="text-2xl md:text-4xl font-bold text-white">
                Chief Technology Officer
              </h2>
              <p className="text-xl md:text-2xl text-gray-300">
                Full-Stack Developer
              </p>
              <div className="h-16 flex justify-center items-center">
                <p className="text-lg text-gray-300 max-w-2xl">
                  {typedText}
                  <span className={`${showHighlight ? 'animate-pulse' : ''} inline-block w-0.5 h-5 bg-primary ml-1`}>|</span>
                </p>
              </div>
            </motion.div>

            {/* Stats Section with improved design */}
            <motion.div 
              className="grid grid-cols-2 md:grid-cols-4 gap-6 mt-12"
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              {stats.map((stat, index) => (
                <motion.div
                  key={index}
                  className="p-6 glass-effect rounded-xl hover:shadow-glow transition-all duration-300 flex flex-col items-center"
                  whileHover={{ scale: 1.05 }}
                >
                  <i className={`${stat.icon} text-2xl text-primary mb-2`}></i>
                  <h3 className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-primary to-info">{stat.number}</h3>
                  <p className="text-gray-300 text-sm mt-1">{stat.label}</p>
                </motion.div>
              ))}
            </motion.div>
            
            {/* CTA Buttons with improved design */}
            <motion.div 
              className="flex flex-col sm:flex-row justify-center gap-6 mt-12"
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.6 }}
            >
              <Link
                to="/about"
                className="group px-8 py-4 rounded-lg font-bold text-white transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg bg-gradient-to-r from-primary to-purple relative overflow-hidden"
              >
                <span className="relative z-10 flex items-center justify-center">
                  <i className="fas fa-user-circle mr-2"></i>
                  View Experience
                </span>
                <div className="absolute inset-0 bg-white/20 transform -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-500"></div>
              </Link>
              <Link
                to="/projects"
                className="group px-8 py-4 rounded-lg font-bold text-white transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg bg-gradient-to-r from-purple to-info relative overflow-hidden"
              >
                <span className="relative z-10 flex items-center justify-center">
                  <i className="fas fa-project-diagram mr-2"></i>
                  View Projects
                </span>
                <div className="absolute inset-0 bg-white/20 transform -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-500"></div>
              </Link>
            </motion.div>

            {/* Social Links with improved design */}
            <motion.div 
              className="mt-12 flex justify-center gap-8"
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.8 }}
            >
              <a 
                href="mailto:mattdarabasz@gmail.com"
                className="social-link"
                title="Email"
              >
                <i className="fas fa-envelope text-2xl"></i>
              </a>
              <a 
                href="tel:215-303-7737"
                className="social-link"
                title="Phone"
              >
                <i className="fas fa-phone text-2xl"></i>
              </a>
              <a 
                href="https://www.linkedin.com/in/matthew-d-758a27168/"
                target="_blank"
                rel="noopener noreferrer"
                className="social-link"
                title="LinkedIn"
              >
                <i className="fab fa-linkedin text-2xl"></i>
              </a>
              <a 
                href="https://github.com/dasz4376"
                target="_blank"
                rel="noopener noreferrer"
                className="social-link"
                title="GitHub"
              >
                <i className="fab fa-github text-2xl"></i>
              </a>
            </motion.div>
          </div>
        </div>

        {/* Scroll Indicator with improved animation */}
        <motion.div 
          className="absolute bottom-10 left-1/2 transform -translate-x-1/2 text-white/70 cursor-pointer"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 1.5, repeat: Infinity }}
          onClick={() => window.scrollTo({ top: window.innerHeight, behavior: 'smooth' })}
        >
          <p className="text-sm mb-2">Scroll Down</p>
          <i className="fas fa-chevron-down animate-bounce"></i>
        </motion.div>
      </motion.section>

      {/* Highlights Section with improved design */}
      <motion.section 
        ref={ref2}
        className="min-h-screen py-20 px-4 bg-gradient-to-b from-slate-900/90 to-slate-800/95 relative"
        initial={{ opacity: 0 }}
        animate={{ opacity: inView ? 1 : 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="max-w-6xl mx-auto">
          <motion.h2 
            className="text-4xl font-bold text-center mb-16 bg-clip-text text-transparent bg-gradient-to-r from-primary to-info"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.6 }}
          >
            Areas of Expertise
          </motion.h2>
          
          <div className="grid md:grid-cols-3 gap-8">
            {highlights.map((item, index) => (
              <motion.div
                key={index}
                className="glass-effect p-8 rounded-xl hover:shadow-glow transition-all duration-300"
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: index * 0.2 }}
                whileHover={{ y: -10 }}
              >
                <div className="text-center mb-6">
                  <i className={`${item.icon} text-4xl text-primary`}></i>
                </div>
                <h3 className="text-xl font-bold text-white mb-4 text-center">{item.title}</h3>
                <p className="text-gray-300 mb-6 text-center">{item.description}</p>
                <div className="flex flex-wrap gap-2 justify-center">
                  {item.tags.map((tag, idx) => (
                    <span
                      key={idx}
                      className="px-3 py-1 bg-primary/20 text-primary rounded-full text-sm"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.section>
    </>
  );
}

export default Home;
